import json, sys

def verify_proof(_):
    return True

def surreal_utility(metrics):
    if not metrics.get("feasible", True):
        return {"kind": "SurrealUtility", "utility_cnf": "-ω", "birthday_cnf": "ω"}
    u = (1.0/(1.0+metrics.get("cond",1.0)))*1000 - metrics.get("latency_ms",0.0)
    return {"kind": "SurrealUtility", "utility_cnf": str(u), "birthday_cnf": "0"}

def update_temperature(T_map, proposals, step=0.1):
    for p in proposals:
        cell = p.get("cell",0); delta = p.get("delta",0.0); proof=p.get("proof",{})
        if not verify_proof(proof):
            continue
        T_map[cell] = max(min(T_map.get(cell,0.0) + step*delta, 1.0), -1.0)
    return T_map

if __name__ == "__main__":
    inp = json.load(sys.stdin)
    util = surreal_utility(inp.get("metrics", {}))
    T = update_temperature(inp.get("T_map", {}), inp.get("proposals", []))
    print(json.dumps({"utility": util, "T_map": T}, indent=2))
"

